import React from 'react';
import Message from '../Message/Message';
import TypedMessage from '../TypedMessage/TypedMessage';
import './Dialog.css';

class Dialog extends React.Component {
    constructor(props) {
        super(props);
        this.state = {messageHistory: []};
    }

    componentDidMount() {
        let history = [
            {"author": "Ivan", content: 'hi'},
            {"author": "Lena", "content": 'how are you'}, 
            {"author": "Ivan", "content": 'I am fine.\nAnd you?'}, 
            {"author": "Lena", "content": "lets go to walk"},
            {"author": "Ivan", "content": "ok"}
        ];
        this.setState({messageHistory: history});
    }

    render() {
        let messages = this.state.messageHistory.map(
            (msg, i) => <Message key={i} author={this.props.author} content={msg.content}></Message>
        );
        return <div className="Dialog">
            <span>dialog</span>
            {messages}
            <TypedMessage></TypedMessage>
            </div>;
    }
}

export default Dialog;
