import React from 'react';
import Message from '../Message/Message';
import TypedMessage from '../TypedMessage/TypedMessage';
import './Dialog.css';

class Dialog extends React.Component {
    constructor(props) {
        super(props);
        this.state = {messageHistory: ""};
    }

    componentDidMount() {
        let history = ['hi', 'how are you', 'I am fine.\nAnd you?', "lets go to walk", "ok"];
        this.setState({messageHistory: history});
    }

    render() {
        let messages = this.state.messageHistory.map((txt, i) => <Message key={i} text={txt}></Message>);
        return <div className="Dialog">
            <span>dialog</span>
            {messages}
            <TypedMessage></TypedMessage>
            </div>;
    }
}

export default Dialog;
