import React from 'react';
import socketIoClient from 'socket.io-client';

class TypedMessage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {typedText: ""};
        this.onChange = this.onChange.bind(this);
        this.onKeyDown = this.onKeyDown.bind(this);
        this.socket = socketIoClient("http://127.0.0.1:4001");
    }

    onChange(evt) {
        let text = evt.target.value;
        this.setState({typedText: text});
    }

    onKeyDown(e) {
        if (e.key === 'Enter') {
            const text = this.state.typedText;
            this.socket.emit("new message", {"author": "I", "content": text});
            this.setState({typedText: ""});
        }
    }

    componentDidMount() {

    }

    render() {
        return <input type="text" 
        value={this.state.typedText}
        onChange={this.onChange}
        onKeyDown={this.onKeyDown}
        ></input>
    }
}

export default TypedMessage;
